import React from "react";
// import { withData } from "./withData"; HOC
import { SpeakersRenderProps } from "./SpeakersRenderProps";

const Speakers = () => {
  <SpeakersRenderProps>
    {(speakers) => {
      return (
        <div>
          {speakers.map(({ imageSrc, name }) => (
            <img src={imageSrc} alt={name} key={imageSrc} />
          ))}
        </div>
      );
    }}
  </SpeakersRenderProps>;
};

// const maxSpeakerToShow = 2;    example of HOC use along with 'withDataHOC'
// export default withData(maxSpeakerToShow)(Speakers);

export default Speakers;
